INSERT INTO `imooc__0__course_info` VALUES ('171', 'Spring Boot 2.0深度实践之系列总览', 'JavaSpringBoot', 'Spring Boot2.0深度实践系列课程总览', '27699', '本课程是SpringBoot2.0深度实践系列课程的总览部分，会向大家揭示如何深度学习SpringBoot2.0,以及介绍SpringBoot2.0深度实践系列课程中的各类议题。SpringBoot2.0深度实践系列课程分上下两篇，上篇核心技术篇已经上线：https://coding.imooc.com/class/252.html下篇生态整合篇敬请期待。', '课程\\后端开发\\Java\\SpringBoot2.0深度实践之系列总览', '高级', ' 2小时10分', '9.40', '27', '9.6', '9.3', '9.3', 'http://www.imooc.com/learn/1058', 'http://www.imooc.com/course/list?page=6', '2020-04-26 15:00:22');
