INSERT INTO `imooc__0__course_info` VALUES ('174', 'SpringBoot构建电商基础秒杀项目', 'Java', '应用SpringBoot快速搭建拥有用户、商品、交易及秒杀活动的电商秒杀应用。', '33343', '本课程中将带领大家，通过使用SpringBoot快速搭建前后端分离的电商基础秒杀项目。项目中会通过应用领域驱动型的分层模型设计方式去完成用户otp注册、登陆、查看、商品列表、进入商品详情以及倒计时秒杀开始后下单购买的基本流程。课程配套进阶实战《Java电商秒杀系统深度优化从容应对亿级流量挑战》已上线https://coding.imooc.com/class/338.html欢迎小伙伴们围观打赏~~另，三少新作《ElasticSearch+Spark构建高相关性搜索服务&千人千面推荐系统》https://coding.imooc.com/class/391.html也上线啦，课程中会结合最新ES7以及spark2.4进行搜索推荐业务的讲解，感兴趣的同学，可以关注一下，欢迎撒花拍砖~~', '课程\\后端开发\\Java\\SpringBoot构建电商基础秒杀项目', '中级', ' 6小时31分', '9.70', '104', '9.9', '9.6', '9.6', 'http://www.imooc.com/learn/1079', 'http://www.imooc.com/course/list?page=5', '2020-04-26 15:00:23');
