INSERT INTO `imooc__0__course_info` VALUES ('203', 'React高级教程之高阶组件', 'React.JS', '运用高阶组件编写高质量React应用', '11475', '高阶组件是React中一个很重要且比较复杂的概念，高阶组件在很多第三方库（如Redux）中都被经常使用。在项目中用好高阶组件，可以显著提高代码质量。', '课程\\前端开发\\React.JS\\React高级教程之高阶组件', '高级', ' 1小时46分', '8.70', '23', '8.4', '8.7', '9.0', 'http://www.imooc.com/learn/1075', 'http://www.imooc.com/course/list?page=5', '2020-04-26 15:00:34');
