INSERT INTO `imooc__0__course_info` VALUES ('206', 'Keras入门', '深度学习人工智能', '深度学习快速入门最佳路径—Keras', '8159', 'Keras是高度模块化，适合初学者使用的深度学习框架，是快速入门深度学习最佳路径。该课程介绍了人工智能及深度学习框架的发展历程，及Keras的基本使用方法，通过快速搭建DNN深度神经网络，实现图片的分类及数值回归预测。', '课程\\前沿技术\\深度学习\\Keras入门', '初级', ' 2小时30分', '9.60', '19', '9.6', '9.2', '10.0', 'http://www.imooc.com/learn/1046', 'http://www.imooc.com/course/list?page=6', '2020-04-26 15:00:35');
