INSERT INTO `imooc__0__course_info` VALUES ('211', '机器学习中的统计学基础', '数学', '机器学习中的常用统计学知识点', '18776', '课程讲解机器学习中的必备统计学基础知识，包含统计学基础、统计学的应用、统计学分类、机器学习中的方差和偏差以及案例分析等。', '课程\\计算机基础\\数学\\机器学习中的统计学基础', '初级', ' 1小时15分', '9.03', '17', '8.5', '9.5', '9.1', 'http://www.imooc.com/learn/1022', 'http://www.imooc.com/course/list?page=7', '2020-04-26 15:00:37');
