INSERT INTO `imooc__0__course_info` VALUES ('216', 'Spring Boot 发送邮件', 'Java', '通过这个课程你将学会如何通过 Spring Boot 设计邮件系统', '11998', '本课程为大家详细介绍了发送邮件的相关知识和原理，介绍了SpringBoot的基础使用，最后为大家演示了如何通过SpringBoot发送各种类型的邮件，掌握如何去设计一个邮件系统。', '课程\\后端开发\\Java\\SpringBoot发送邮件', '中级', ' 1小时27分', '9.40', '36', '9.8', '9.1', '9.3', 'http://www.imooc.com/learn/1036', 'http://www.imooc.com/course/list?page=6', '2020-04-26 15:00:39');
