INSERT INTO `imooc__0__course_info` VALUES ('218', '《MAYA-场景设置》', '动效动画', '快速入门 MAYA场景设置', '2412', '本套教程主要是讲解场景中材质制作的的基础内容，讲解过程当中作者会根据工作经验以及材质的理解，给大家解释一些CG行业常遇到的问题。最后的章节，会带领大家制作一个案例，来巩固学习。', '课程\\UI设计&多媒体\\动效动画\\《MAYA-场景设置》', '入门', ' 1小时23分', '9.10', '3', '10.0', '9.3', '8.0', 'http://www.imooc.com/learn/1013', 'http://www.imooc.com/course/list?page=7', '2020-04-26 15:00:40');
INSERT INTO `imooc__0__course_info` VALUES ('219', 'C4D地面多边形建模', '模型制作', 'C4D初期低面多边形建模', '3257', '在建模初期我们要观察这个模型，了解它的结构，从大着眼，从小入手，然后改变它的外部结构使之形成低面多边形风格', '课程\\UI设计&多媒体\\模型制作\\C4D地面多边形建模', '初级', ' 2小时36分', '9.60', '5', '8.8', '10.0', '10.0', 'http://www.imooc.com/learn/987', 'http://www.imooc.com/course/list?page=8', '2020-04-26 15:00:40');
INSERT INTO `imooc__0__course_info` VALUES ('220', '《MAYA-Vary 渲染》', '动效动画', 'Vray For Maya基础教学', '1719', '本套教程主要是讲解Vray渲染器的基础内容，讲解过程当中作者会根据工作经验和对Vray的理解，给大家解释一些CG行业常遇到的问题。还有Vray在工作中的常规设置和使用。最后的章节，会带领大家制作一个案例，来巩固学习。', '课程\\UI设计&多媒体\\动效动画\\《MAYA-Vary渲染》', '入门', ' 1小时34分', '10.00', '2', '10.0', '10.0', '10.0', 'http://www.imooc.com/learn/1014', 'http://www.imooc.com/course/list?page=7', '2020-04-26 15:00:40');
