INSERT INTO `imooc__0__course_info` VALUES ('222', 'Storm基础入门', 'JavaStorm', '带你快速入门Storm,对数据进行实时流式计算', '12754', 'Storm作为实时流式计算中的佼佼者，因其良好的特性使其使用场景非常广泛。Zookeeper作为分布式协调服务框架，因其完善的数据一致性保证特性使其成为各框架必备组件。本课分为三大部分：第一部分介绍Storm的起源背景、使用场景及特性；第二部分了解Storm的架构体系及组件作用；第三部分介绍Zookeeper的基础知识及动手实践Zookeeper安装配置与集群搭建；第四部分动手实践Storm安装配置与集群搭建。', '课程\\后端开发\\Java\\Storm基础入门', '初级', ' 1小时19分', '9.10', '18', '9.1', '8.8', '9.4', 'http://www.imooc.com/learn/991', 'http://www.imooc.com/course/list?page=8', '2020-04-26 15:00:42');
