INSERT INTO `imooc__0__course_info` VALUES ('290', '前端内容的自动化构建', '前端工具', '手把手教你如何自动化构建JavaScript和CSS', '20853', '随着前端技术的快速发展，前端内容在整个web开发中所占的比例越来越重。本课将为你解答如何自动化构建前端内容，提升我们的前端开发效率。本课程分为三个部分，第一部分讲解什么是构建，为什么需要自动化构建，后面两部分分别讲解如何使用Gulp进行JavaScript和CSS的构建。', '课程\\前端开发\\前端工具\\前端内容的自动化构建', '中级', ' 2小时30分', '9.20', '22', '9.5', '8.9', '9.2', 'http://www.imooc.com/learn/922', 'http://www.imooc.com/course/list?page=10', '2020-04-26 15:01:06');
