INSERT INTO `imooc__0__course_info` VALUES ('382', 'APP性能优化之内存优化', 'Android', '内存优化的原理，实战并有代码演示', '16098', '1、讲解Android系统内存管理方式里的内存分配回收，并实战演示监控内存的方法。2、讲解APP优化的几种方法，主要涉及数据结构优化、对象复用优化、内存泄漏优化方法，最后是演示效果。3、APP内存优化里主要的OOM问题进行剖析，讲解优化方法和方案，并实战代码讲解效果。', '课程\\移动开发\\Android\\APP性能优化之内存优化', '中级', ' 1小时15分', '9.67', '25', '9.9', '9.6', '9.5', 'http://www.imooc.com/learn/777', 'http://www.imooc.com/course/list?page=14', '2020-04-26 15:01:43');
