INSERT INTO `imooc__0__course_info` VALUES ('553', 'Hibernate注解', 'Java', '轻松学习Hibernate常用注解，学完本课能够灵活应用', '37580', '注解在Java程序开发中的应用越来越广泛，可以有效地提高代码的简洁度。本门课程主要介绍Hibernate的常用注解，包括类级别注解、属性级别注解和关联映射注解。希望小伙伴们学习完本门课程后，能够灵活应用Hibernate注解进行开发。', '课程\\后端开发\\Java\\Hibernate注解', '中级', ' 2小时33分', '9.57', '105', '9.8', '9.5', '9.4', 'http://www.imooc.com/learn/524', 'http://www.imooc.com/course/list?page=19', '2020-04-26 15:02:50');
