INSERT INTO `imooc__0__course_info` VALUES ('617', 'Cocos2d-x游戏开发入门－贪吃蛇(下)', 'Cocos2d-x', '本课程将对上一门课进行改造，加入新的元素，符合游戏特性', '15069', '本课程将在贪吃蛇游戏的基础上加入一些新的元素，从而符合当下游戏的特性。至于是什么新元素，学了你就知道', '课程\\游戏\\Cocos2d-x\\Cocos2d-x游戏开发入门－贪吃蛇(下)', '中级', ' 1小时37分', '9.67', '10', '10.0', '9.8', '9.2', 'http://www.imooc.com/learn/508', 'http://www.imooc.com/course/list?page=21', '2020-04-26 15:03:15');
