INSERT INTO `imooc__0__course_info` VALUES ('619', 'Android摄像头基础', 'Android', '带你走进Android摄像头的秘密花园，探索Camera的奥秘', '41176', '本课程为Android摄像头的课程讲解。由浅入深，带领小伙伴们探索Camera的奥秘，带大家学会摄像头的摄像原理和底层的实现。', '课程\\移动开发\\Android\\Android摄像头基础', '高级', ' 1小时23分', '9.70', '66', '9.9', '9.7', '9.5', 'http://www.imooc.com/learn/543', 'http://www.imooc.com/course/list?page=20', '2020-04-26 15:03:16');
