INSERT INTO `imooc__0__course_info` VALUES ('623', 'Spring MVC起步', 'Java', 'Java中Spring MVC框架入门教程，快来看最易用的MVC框架', '189466', 'SpringMVC为我们提供了一个基于组件和松耦合的MVC实现框架。在使用Java中其它MVC框架多年之后，面对SpringMVC有一种相见恨晚的感觉。SpringMVC是如此的优雅，轻盈与简洁，让人从其它框架的桎梏解脱出来。本课程将带你步入SpringMVC。', '课程\\后端开发\\Java\\SpringMVC起步', '中级', ' 2小时 6分', '8.40', '524', '8.9', '8.2', '8.1', 'http://www.imooc.com/learn/47', 'http://www.imooc.com/course/list?page=23', '2020-04-26 15:03:17');
