INSERT INTO `imooc__0__course_info` VALUES ('851', '可调大小面板实现（Resizeable Panel）', 'JavaScript', '前端UI组件仓库必备，先从分析实现原理到步骤分解，走完全过程', '12790', 'QQ空间自定义模板可以自由调整其大小，这个课程来揭秘其中的原理，先从分析实现原理到步骤分解，再到关键代码解析，最后详细讲解每个步骤的代码实现过程。一步步带领小伙伴们实现ResizablePanel的开发。', '课程\\前端开发\\JavaScript\\可调大小面板实现（ResizeablePanel）', '初级', ' 1小时 0分', '9.70', '45', '9.9', '9.6', '9.6', 'http://www.imooc.com/learn/193', 'http://www.imooc.com/course/list?page=28', '2020-04-26 15:04:40');
